import React from 'react';
import { getCurrentUser } from '../services/auth';
import Watchlist from '../components/Watchlist';
import Rating from '../components/Rating';

const Profile = () => {
  const user = getCurrentUser();

  if (!user) {
    return (
      <div className="flex justify-center items-center h-screen">
        <p className="text-xl">You are not logged in.</p>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <h1>{user.username}'s Profile</h1>
      <p>Email: {user.email}</p>
      <h2>Favorites</h2>
      <ul>
        {user.favorites.map((movie) => (
          <li key={movie.id}>{movie.title}</li>
        ))}
      </ul>
      <h2>Watchlist</h2>
      {/* Assuming Watchlist is a component that takes a list of movies */}
      <Watchlist watchlist={user.watchlist} />
      <h2>Rate Movies</h2>
      {/* Assuming Rating is a component that allows users to rate movies */}
      <Rating />
    </div>
  );
};

export default Profile;
